mapper.update:
UPDATE article
SET title = #{title}, content = #{content}
WHERE id = #{id};

UPDATE article
SET status = 2
WHERE id = #{id};

mapper.select:
SELECT COUNT(1)
FROM comment
WHERE article_id = #{id};

SELECT COUNT(1)
FROM article_like
WHERE article_id = #{id};

SELECT nickname
FROM user
WHERE id = #{id};
